import process from 'node:process';
import { d as defineCommand, l as logger } from '../shared/nuxi.vClchuwn.mjs';
import { s as showVersions } from '../shared/nuxi.CZkFAOfB.mjs';
import { o as overrideEnv } from '../shared/nuxi.onla3I0R.mjs';
import { a as clearBuildDir } from '../shared/nuxi.CVt1GvGe.mjs';
import { l as loadKit } from '../shared/nuxi.BTNtCwAV.mjs';
import { c as cwdArgs, l as logLevelArgs, d as dotEnvArgs, e as envNameArgs, a as legacyRootDirArgs } from '../shared/nuxi.DpekDEZq.mjs';
import { r as resolve, a as relative } from '../shared/nuxi.BHLUJ6h2.mjs';
import 'node:util';
import 'node:path';
import 'node:tty';
import 'node:url';
import '../shared/nuxi.CQKunj3-.mjs';
import 'node:fs';
import 'node:module';
import '../shared/nuxi.ChFrgAY-.mjs';
import 'node:assert';
import 'node:v8';
import '../shared/nuxi.BZI8rqcO.mjs';
import '../shared/nuxi.oF0sJJb9.mjs';
import '../shared/nuxi.E-ZsRS8r.mjs';
import 'node:os';
import 'node:crypto';
import 'node:perf_hooks';
import 'node:vm';
import 'assert';
import 'fs';
import 'module';
import 'os';
import 'path';
import 'process';
import 'tty';
import 'url';
import 'util';
import 'v8';

const buildCommand = defineCommand({
  meta: {
    name: "build",
    description: "Build Nuxt for production deployment"
  },
  args: {
    ...cwdArgs,
    ...logLevelArgs,
    prerender: {
      type: "boolean",
      description: "Build Nuxt and prerender static routes"
    },
    preset: {
      type: "string",
      description: "Nitro server preset"
    },
    ...dotEnvArgs,
    ...envNameArgs,
    ...legacyRootDirArgs
  },
  async run(ctx) {
    overrideEnv("production");
    const cwd = resolve(ctx.args.cwd || ctx.args.rootDir);
    await showVersions(cwd);
    const kit = await loadKit(cwd);
    const nuxt = await kit.loadNuxt({
      cwd,
      dotenv: {
        cwd,
        fileName: ctx.args.dotenv
      },
      envName: ctx.args.envName,
      // c12 will fall back to NODE_ENV
      overrides: {
        logLevel: ctx.args.logLevel,
        // TODO: remove in 3.8
        _generate: ctx.args.prerender,
        nitro: {
          static: ctx.args.prerender,
          preset: ctx.args.preset || process.env.NITRO_PRESET || process.env.SERVER_PRESET
        },
        ...ctx.data?.overrides
      }
    });
    let nitro;
    try {
      nitro = kit.useNitro?.();
      logger.info(`Building for Nitro preset: \`${nitro.options.preset}\``);
    } catch {
    }
    await clearBuildDir(nuxt.options.buildDir);
    await kit.writeTypes(nuxt);
    nuxt.hook("build:error", (err) => {
      logger.error("Nuxt Build Error:", err);
      process.exit(1);
    });
    await kit.buildNuxt(nuxt);
    if (ctx.args.prerender) {
      if (!nuxt.options.ssr) {
        logger.warn(
          "HTML content not prerendered because `ssr: false` was set. You can read more in `https://nuxt.com/docs/getting-started/deployment#static-hosting`."
        );
      }
      const dir = nitro?.options.output.publicDir;
      const publicDir = dir ? relative(process.cwd(), dir) : ".output/public";
      logger.success(
        `You can now deploy \`${publicDir}\` to any static hosting!`
      );
    }
  }
});

export { buildCommand as default };
