import process from 'node:process';
import { d as defineCommand, l as logger } from '../shared/nuxi.vClchuwn.mjs';
import { a as clearBuildDir } from '../shared/nuxi.CVt1GvGe.mjs';
import { l as loadKit } from '../shared/nuxi.BTNtCwAV.mjs';
import { d as dotEnvArgs, c as cwdArgs, l as logLevelArgs, e as envNameArgs, a as legacyRootDirArgs } from '../shared/nuxi.DpekDEZq.mjs';
import { r as resolve, a as relative } from '../shared/nuxi.BHLUJ6h2.mjs';
import 'node:util';
import 'node:path';
import 'node:tty';
import 'node:url';
import 'node:fs';
import 'node:module';
import '../shared/nuxi.oF0sJJb9.mjs';
import '../shared/nuxi.E-ZsRS8r.mjs';
import 'node:os';
import 'node:assert';
import 'node:v8';
import 'node:crypto';
import 'node:perf_hooks';
import 'node:vm';
import 'assert';
import 'fs';
import 'module';
import 'os';
import 'path';
import 'process';
import 'tty';
import 'url';
import 'util';
import 'v8';

const prepare = defineCommand({
  meta: {
    name: "prepare",
    description: "Prepare Nuxt for development/build"
  },
  args: {
    ...dotEnvArgs,
    ...cwdArgs,
    ...logLevelArgs,
    ...envNameArgs,
    ...legacyRootDirArgs
  },
  async run(ctx) {
    process.env.NODE_ENV = process.env.NODE_ENV || "production";
    const cwd = resolve(ctx.args.cwd || ctx.args.rootDir);
    const { loadNuxt, buildNuxt, writeTypes } = await loadKit(cwd);
    const nuxt = await loadNuxt({
      cwd,
      dotenv: {
        cwd,
        fileName: ctx.args.dotenv
      },
      envName: ctx.args.envName,
      // c12 will fall back to NODE_ENV
      overrides: {
        _prepare: true,
        logLevel: ctx.args.logLevel,
        ...ctx.data?.overrides
      }
    });
    await clearBuildDir(nuxt.options.buildDir);
    await buildNuxt(nuxt);
    await writeTypes(nuxt);
    logger.success(
      "Types generated in",
      relative(process.cwd(), nuxt.options.buildDir)
    );
  }
});

export { prepare as default };
